import React, { useState } from 'react';
import axios from 'axios';

const Register = ({ onShowRegister, onShowLogin }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/users/register', { username, email, password });
      // Optionally log in the user immediately after registration
      const loginResponse = await axios.post('http://localhost:3000/api/users/login', { email, password });
      onShowRegister(loginResponse.data.token);
    } catch (error) {
      console.error('Registration failed:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* ... existing inputs ... */}
      <button type="submit">Register</button>
      <button type="button" onClick={onShowLogin}>Back to Login</button>
    </form>
  );
};

export default Register;
